    //     //        })
    //     }
    //     catch (error){
    //         console.error(error);
    //     }
    // };
    // handleChange = event =>{
    //     const {name, value} = event.target;

    //     this.setState({[name]: value}) ;
    // };
   
                <form className='sign-up-form' onSubmit={this.handleSubmit}>
                    <FormInput 
                    type='text'
                    name='displayName'
                    value={displayName}
                    onChange={this.handleSubmit}
                    label='Dispaly name'
                    required
                    />

                        
                   
                    <FormInput 
                    type='email'
                    name='email'
                    value={email}
                    onChange={this.handleSubmit}
                    label='Email'
                    required
                    />
                   


                    <FormInput 
                    type='password'
                    name='password'
                    value={password}
                    onChange={this.handleSubmit}
                    label='password'
                    required
                    />
                    

                    <FormInput 
                    type='password'
                    name='confirmPassword'
                    value={confirmPassword}
                    onChange={this.handleSubmit}
                    label='ConfirmPassword'
                    required
                    />
                    
                    <CustomButton type='submit'> SIGN UP </CustomButton>
                </form>
            
        
    }
}

export default Signup
